import maya.cmds as cmds

def fxGlobalSetup():
    
    if not cmds.objExists('___FX___'):
        name = cmds.group(em=True,n='___FX___')
        return name
    else:
        return '___FX___'
	
def fxRigInit():

	fxGroup = fxGlobalSetup()
	
	root = cmds.group(em=True,n='root_')
	root_root = cmds.group(em=True,n='root_root')
		
	world = squareControl(name='WORLD')
	worldShape = cmds.listRelatives(world,s=True)[0]
	c_root = crownControl(name='c_root')
	c_rootShape = cmds.listRelatives(c_root,s=True)[0]
	
	cmds.setAttr(c_root+'.sx',.5)
	cmds.setAttr(c_root+'.sy',.5)
	cmds.setAttr(c_root+'.sz',.5)
	
	cmds.makeIdentity(c_root, apply=True, t=1, r=1, s=1, n=0)
	
	cmds.setAttr(c_rootShape+'.overrideEnabled',1)
	cmds.setAttr(c_rootShape+'.overrideColor',13)
	
	cmds.setAttr(worldShape+'.overrideEnabled',1)
	cmds.setAttr(worldShape+'.overrideColor',17)
	
	cmds.parent(fxGroup,c_root)
	cmds.parent(c_root,root_root)
	cmds.parent(root_root,world)
	cmds.parent(world,root)
	
	cmds.select(c_root,add=True)
	
	cmds.sets(name='ctrlSet')
	
	return root
	
