def squareControl(name=None):
	list = []
	list.append(cmds.curve( p =[(6,0,-6),(6,0,6),(-6,0,6),(-6,0,-6)],per = False, d=1,n=name))
	cmds.closeCurve(list[0],ch=0,ps=1,rpo=1,bb=0.5,bki=0,p=0.1)
	
	if name:
		outName = cmds.rename(list[0],name)
		return outName
	else:
		return list[0]

